import React, { useState } from "react";
import axios from "axios";
import LoadingSpinner from "./loader";
import {  Link , useNavigate } from "react-router-dom";
import { toast, ToastContainer } from "react-toastify";
import {
  Stack,
  TextField,
  IconButton,
  InputAdornment,
} from "@mui/material";
import { LoadingButton } from "@mui/lab";
import { Icon } from "@iconify/react";
import { motion } from "framer-motion";
const LoginForm = () => {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [error, setError] = useState("");
  const [isLoading, setIsLoading] = useState(false);
  const navigate = useNavigate();
  const [showGoogleLogin, setShowGoogleLogin] = useState(false);
  const [showPassword, setShowPassword] = useState(false);

  let easing = [0.6, -0.05, 0.01, 0.99];
  const animate = {
    opacity: 1,
    y: 0,
    transition: {
      duration: 0.6,
      ease: easing,
      delay: 0.16,
    },
  };

  const handleLogin = async () => {
  setIsLoading(true);
  setError("");
  try {
    const response = await axios.post("http://localhost:5000/login", {
      email,
      password,
    });

    const token = response.data.token;
    localStorage.setItem("jwtToken", token);
    setIsLoading(false);
    setEmail("");
    setPassword("");
    setError("");
    navigate("/");
  } catch (error) {
    console.error('Login error:', error);
    if (error.response) {
      // The request was made and the server responded with a status code
      // that falls out of the range of 2xx
      console.error('Server response:', error.response.data);
      console.error('Status code:', error.response.status);
      console.error('Headers:', error.response.headers);

      // Display an error message to the user based on the server response
      if (error.response.status === 401) {
        toast.error("Invalid email or password");
      } else if (error.response.status === 400) {
        toast.error("Email not verified");
      } else {
        toast.error("An error occurred while logging in");
      }
    } else if (error.request) {
      // The request was made but no response was received
      console.error('No response received from the server');
      toast.error("No response received from the server");
    } else {
      // Something happened in setting up the request that triggered an Error
      console.error('Error setting up the request:', error.message);
      toast.error("An error occurred while logging in");
    }

    setIsLoading(false);
  }
};

  return (
<div>

                      <div className="form-outline mb-4">
                        <input
                          type="email"
                          id="form2Example11"
                          className="form-control"
                          placeholder="Phone number or email address"
                          value={email}
                          onChange={(e) => setEmail(e.target.value)}
                        />
                        <label className="form-label" for="form2Example11">
                          Username
                        </label>
                      </div>

                      <div className="form-outline mb-4">
                        <input
                          type="password"
                          id="form2Example22"
                          className="form-control"
                          value={password}
                          onChange={(e) => setPassword(e.target.value)}
                          placeholder="Enter Password"
                        />
                
                        <label className="form-label" for="form2Example22">
                          Password
                        </label>
                      </div>

                      <div className="text-center pt-1 mb-5 pb-1">
                        
                          <button
                            className="gradient-custom-2 my-1"
                            type="button"
                            onClick={handleLogin}
                          >
                            Sign in
                          </button>
                      
                     </div>

                     <div>
      <ToastContainer
        position="top-right"
        autoClose={5000}
        hideProgressBar={false}
        newestOnTop={false}
        closeOnClick
        rtl={false}
        pauseOnFocusLoss
        draggable
        pauseOnHover
        theme="light"
      />
      <form autoComplete="off" noValidate onSubmit={""}>
        <Stack spacing={3}>
          <Stack
            component={motion.div}
            initial={{ opacity: 0, y: 60 }}
            animate={animate}
            direction={{ xs: "column", sm: "row" }}
            spacing={2}
          >
            <TextField
              fullWidth
              label="First name"
              name="firstName"
              value={formData.firstName}
              onChange={handleInputChange}
              error={Boolean(formData.firstName === "" && error)}
              helperText={formData.firstName === "" && error}
            />

            <TextField
              fullWidth
              label="Last name"
              name="lastName"
              value={formData.lastName}
              onChange={handleInputChange}
              error={Boolean(formData.lastName === "" && error)}
              helperText={formData.lastName === "" && error}
            />
          </Stack>

          <Stack
            spacing={3}
            component={motion.div}
            initial={{ opacity: 0, y: 40 }}
            animate={animate}
          >
            <TextField
              fullWidth
              autoComplete="username"
              type="email"
              label="Email address"
              name="email"
              value={formData.email}
              onChange={handleInputChange}
              error={Boolean(formData.email === "" && error)}
              helperText={formData.email === "" && error}
            />

            <TextField
              fullWidth
              autoComplete="new-password"
              type={showPassword ? "text" : "password"}
              label="Password"
              name="password"
              value={formData.password}
              onChange={handleInputChange}
              error={Boolean(formData.password === "" && error)}
              helperText={formData.password === "" && error}
              InputProps={{
                endAdornment: (
                  <InputAdornment position="end">
                    <IconButton
                      edge="end"
                      onClick={() => setShowPassword((prev) => !prev)}
                    >
                      <Icon
                        icon={
                          showPassword ? "eva:eye-fill" : "eva:eye-off-fill"
                        }
                      />
                    </IconButton>
                  </InputAdornment>
                ),
              }}
            />
          </Stack>

          <Stack
            component={motion.div}
            initial={{ opacity: 0, y: 20 }}
            animate={animate}
            style={{ marginBottom: "21px" }}
          >
            <TextField
              fullWidth
              autoComplete="new-password"
              type="password"
              label="Confirm Password"
              name="cpassword"
              value={formData.cpassword}
              onChange={handleInputChange}
              error={Boolean(formData.cpassword === "" && error)}
              helperText={formData.cpassword === "" && error}
            />

            <LoadingButton
              style={{ marginTop: "20px" }}
              fullWidth
              size="large"
              type="submit"
              variant="contained"
            >
              {isLoading ? <LoadingSpinner /> : "Sign up"}
            </LoadingButton>
          </Stack>
        </Stack>
      </form>
    </div>




                 </div>    
 
  );
};

export default LoginForm;
