import React, { useState } from "react";
import axios from "axios";
import LoadingSpinner from "./loader";
import { Form, Link as RouterLink, useNavigate } from "react-router-dom";
import { Link } from "react-router-dom";
import {
  Box,
  IconButton,
  InputAdornment,
  Stack,
  TextField,
} from "@mui/material";
import { LoadingButton } from "@mui/lab";
import { Icon } from "@iconify/react";
import { motion } from "framer-motion";
import { toast, ToastContainer } from "react-toastify";

const LoginForm = () => {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [error, setError] = useState("");
  const [isLoading, setIsLoading] = useState(false);
  const navigate = useNavigate();
  const [showGoogleLogin, setShowGoogleLogin] = useState(false);
  const [showPassword, setShowPassword] = useState(false);

  let easing = [0.6, -0.05, 0.01, 0.99];
  const animate = {
    opacity: 1,
    y: 0,
    transition: {
      duration: 0.6,
      ease: easing,
      delay: 0.16,
    },
  };

  const handleLogin = async () => {
    setIsLoading(true);
    setError("");
    try {
      const response = await axios.post("http://localhost:5000/login", {
        email,
        password,
      });

      if (response.data.isVerified === false) {
        toast.error("Email not verified");
        setIsLoading(false);
        return;
      }

      const token = response.data.token;
      localStorage.setItem("jwtToken", token);
      setIsLoading(false);
      setEmail("");
      setPassword("");
      setError("");
      navigate("/");
    } catch (error) {
      if (error.response && error.response.status === 401) {
        toast.error("Invalid email or password");
      } else if (error.response && error.response.status === 400) {
        toast.error("Email not verified");
      } else {
        toast.error("An error occurred while logging in");
      }
      setIsLoading(false);
    }
  };

  return (
    <section style={{ overflow: "hidden" }}>
      {/* // <section className="h-212 gradient-form w-100" style={{backgroundColor:"#eee", overflow:"hidden"}}> */}
      <div className="container mx-auto px-auto py-5 h-100">
        <div className="row d-flex justify-content-center align-items-center h-100">
          <div className="col-xl-6">
            <div className="card rounded-3 text-black">
              <div className="row g-0">
                <div className="">
                  <div className="card-body p-md-5 mx-md-4">
                    <div className="text-center">
                      <img
                       
                        style={{ width: "303px", margin: "-103px" }}
                        alt="logo"
                      />
                      <h4
                        className="mt-1 mb-5 pb-1"
                        style={{
                          fontSize: "30px",
                          fontWeight: "700",
                        }}
                      >
                        Welcome To MAGVA...
                      </h4>
                    </div>

                    <form>
                      <p>Please login to your account</p>

                      <div className="form-outline mb-4">
                        <input
                          type="email"
                          id="form2Example11"
                          className="form-control"
                          placeholder="Phone number or email address"
                          value={email}
                          onChange={(e) => setEmail(e.target.value)}
                        />
                        <label className="form-label" for="form2Example11">
                          Username
                        </label>
                      </div>

                      <div className="form-outline mb-4">
                        <input
                          type="password"
                          id="form2Example22"
                          className="form-control"
                          value={password}
                          onChange={(e) => setPassword(e.target.value)}
                          placeholder="Enter Password"
                        />
                
                        <label className="form-label" for="form2Example22">
                          Password
                        </label>
                      </div>

                      <div className="text-center pt-1 mb-5 pb-1">
                        <a href="index1.html">
                          <button
                            className="gradient-custom-2 my-1"
                            type="button"
                            onClick={handleLogin}
                          >
                            Sign in
                          </button>
                        </a>
                        <br></br>
                        <a className="text-muted my-2" >
                      <Link to="/reset"><u>Forgot password?</u></Link>
                        </a>
                      </div>

                      <div className="d-flex justify-content-center text-center mt-4 pt-1">
                        <div className="logo">
                          <a
                            
                            className="text-black"
                          >
                            <i className="fab fa-facebook-f fa-lg"></i>
                          </a>
                          <a  className="text-black">
                            <i className="fab fa-twitter fa-lg mx-4 px-2"></i>
                          </a>
                          <a onClick={signInWithGoogle} className="text-black">
                            <i className="fab fa-google fa-lg"></i>
                          </a>
                        </div>
                      </div>
                      <div className="d-flex align-items-center justify-content-center pb-4">
                        <p className="mb-0 me-2">Don't have an account?</p>
                        <a  className="fw-bold text-body">
                        <Link to="/signup"> <u>Register here</u></Link>
                        </a>
                      </div>
                    </form>
                  </div>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
      {/* // </section> */}
    </section>
  );
};

export default LoginForm;
