import React, { useState, useEffect } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";
import { toast } from "react-toastify";
import "../App.css"
import { ToastContainer } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';

const LoginForm = () => {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [error, setError] = useState("");
  const [isLoading, setIsLoading] = useState(false);
  const navigate = useNavigate();
  const [signIn, setSignIn] = useState(true);
  const [redirectToHome, setRedirectToHome] = useState(false);

  const handleSignInClick = () => {
    setSignIn((prevSignIn) => !prevSignIn);
  };

  const handleSignUpClick = () => {
    setSignIn((prevSignIn) => !prevSignIn);
  };

  const handleLogin = async () => {
    setIsLoading(true);
    setError("");
    try {
      const response = await axios.post("https://innoriknews.onrender.com/login", {
        email,
        password,
      });

      const token = response.data.token;
      localStorage.setItem("jwtToken", token);
      setIsLoading(false);
      setEmail("");
      setPassword("");
      setError("");
      setRedirectToHome(true);
    } catch (error) {
      console.error("Login error:", error);
      if (error.response) {
        if (error.response.status === 401) {
          toast.error("Invalid email or password");
        } else if (error.response.status === 400) {
          toast.error("Email not verified");
        } else {
          toast.error("An error occurred while logging in");
        }
      } else if (error.request) {
        console.error("No response received from the server");
        toast.error("No response received from the server");
      } else {
        console.error("Error setting up the request:", error.message);
        toast.error("An error occurred while logging in");
      }

      setIsLoading(false);
    }
  };

  // Add this at the end of your component
  useEffect(() => {
    if (redirectToHome) {
      navigate("/", { replace: true });
    }
  }, [redirectToHome]);

  const [formData, setFormData] = useState({
    name: "",
    email: "",
    password: "",
    cpassword: "",
  });

  const handleSignupFormSubmit = async (e) => {
    e.preventDefault(); // Prevent the default form submission behavior
    setIsLoading(true);
    try {
      const response = await axios.post("https://innoriknews.onrender.com/signup", {
        name: formData.name,
        email: formData.email,
        password: formData.password,
        cpassword: formData.cpassword,
      });
  
      console.log("Server response:", response.data);
      toast.success("Signup successful. Please log in.");
  
      navigate("/user");
      setFormData({
        name: "",
        email: "",
        password: "",
        cpassword: "",
      });
      setError("");
    } catch (error) {
      if (error.response && error.response.status === 409) {
        toast.error("Email already registered");
      } else if (error.response && error.response.status === 401) {
        toast.error("Password does not match");
      } else {
        toast.error("Error signing up:", error.message);
      }
    } finally {
      setIsLoading(false);
    }
  };
  
  return (
   <div className="container">
    <div className="header">
      <div className="text">Sign up</div>
       <div className="underline"></div>
    </div>
    <div className="">

    </div>
    
   </div>
  );
};

export default LoginForm;
