import React, { useEffect, useState } from "react";
import axios from "axios";
import "../App.css";
import { toast, ToastContainer } from "react-toastify";
import "react-toastify/dist/ReactToastify.css";
import { Link, useNavigate } from "react-router-dom";

axios.defaults.baseURL = "https://innorik.onrender.com";

const NewsFeed = ({ interests }) => {
  const navigate = useNavigate();
  const [news, setNews] = useState([]);
  const [filteredNews, setFilteredNews] = useState([]);
  const [savedArticles, setSavedArticles] = useState([]);
  useEffect(() => {
    async function fetchNews() {
      const apiKey = "122a28fdd165418d917fc4c240832384";
      const url = `https://newsapi.org/v2/top-headlines?country=us&apiKey=${apiKey}`;

      try {
        const response = await axios.get(url);
        setNews(
          response.data.articles.map((article) => ({
            ...article,
            isSaved: false,
          }))
        );
      } catch (error) {
        console.error("Error fetching news:", error);
      }
    }

    fetchNews();
  }, []);
  useEffect(() => {
    async function fetchSavedArticles() {
      try {
        const response = await axios.get("/api/articles/saved", {
          headers: {
            Authorization: `Bearer ${localStorage.getItem("jwtToken")}`,
          },
        });

        setSavedArticles(response.data);
      } catch (error) {
        console.error("Error fetching saved articles:", error);
      }
    }

    fetchSavedArticles();
  }, []);

  useEffect(() => {
    if (interests.length > 0) {
      const filteredNews = news.filter(
        (article) =>
          article.content &&
          interests.some((interest) =>
            article.content.toLowerCase().includes(interest)
          )
      );
      setFilteredNews(filteredNews);
    } else {
      setFilteredNews(news);
    }
  }, [interests, news]);

  const handleSaveArticle = async (article) => {
    const token = localStorage.getItem("jwtToken");
    if (!token) {
      navigate.push("/login");
      return;
    }
    try {
      const token = localStorage.getItem("jwtToken");
      const response = await axios.post(
        "/api/articles",
        {
          link: article.url,
          title: article.title,
          image: article.urlToImage,
          description: article.description,
        },
        {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        }
      );
      toast.success("Article Saved Successful");
      console.log(response.data.message);
      setSavedArticles([...savedArticles, response.data.savedArticle]);
    } catch (error) {
      console.error("Save article error:", error);
      toast.error("Article Already Saved");
    }
  };

  const handleToggleSave = async (article) => {
    try {
      await axios.delete(`/api/articles/${article._id}`, {
        headers: {
          Authorization: `Bearer ${localStorage.getItem("jwtToken")}`,
        },
      });

      setSavedArticles(
        savedArticles.filter((saved) => saved._id !== article._id)
      );
    } catch (error) {
      console.error("Unsave article error:", error);
    }
  };
  return (
    <div className="news-articles-container" >
      <h2>News Feed</h2>
      <ToastContainer
        position="top-right"
        autoClose={5000}
        hideProgressBar={false}
        newestOnTop={false}
        closeOnClick
        rtl={false}
        pauseOnFocusLoss
        draggable
        pauseOnHover
        theme="light"
      />
      <div className="grid-container">
        {filteredNews.map(
          (article) =>
            article.urlToImage &&
            article.description && (
              <div key={article.url} className="card">
                <img
                  src={article.urlToImage}
                  className="card-img-top"
                  alt="..."
                />
                <div className="card-body">
                  <h5 className="card-title">{article.title}</h5>
                  <p className="card-text" style={{ textAlign: "left" }}>
                    {article.description.slice(0, 150)}.....
                  </p>
                </div>
                <div className="card-body">
                  <a href={article.url} className="hero-btn1">
                    Read more
                  </a>
                  {localStorage.getItem("jwtToken") ? (
                    article.isSaved ? (
                      <button
                        className="hero-btn1"
                        onClick={() => handleToggleSave(article)}
                      >
                        Unsave
                      </button>
                    ) : (
                      <button
                        className="hero-btn1"
                        onClick={() => handleSaveArticle(article)}
                      >
                        Save
                      </button>
                    )
                  ) : (
                    <Link to="/login" className="hero-btn1">
                      Log in
                    </Link>
                  )}
                </div>
              </div>
            )
        )}
      </div>
    </div>
  );
};

export default NewsFeed;
